    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       Addaction price = new Addaction();
       price.hotDog = price.hotDogUnitPrice * Double.parseDouble( jTextField1.getText());
       price.hamBurger = price.hamBurgerUnitPrice * Double.parseDouble(jTextField2.getText());
       price.frenchFries = price.frenchFriesUnitPrice * Double.parseDouble(jTextField3.getText());
       price.pizza = price.pizzaUnitPrice * Double.parseDouble(jTextField4.getText());
       
       price.mojito = price.mojitoUnitPrice * Double.parseDouble( jTextField5.getText());
       price.milkShake = price.milkShakeUnitPrice * Double.parseDouble(jTextField6.getText());
       price.cappuccino = price.cappuccinoUnitPrice * Double.parseDouble(jTextField7.getText());
       
       jTextField10.setText("$"+price.sum());
       jTextField8.setText("$"+price.foodPrice);
       jTextField9.setText("$"+price.fluidPrice);
       
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Calendar c = Calendar.getInstance();
        
        SimpleDateFormat T = new SimpleDateFormat("HH:mm:ss");
        T.format(c.getTime());
        
        SimpleDateFormat D = new SimpleDateFormat("dd:MM:yyyy");
        D.format(c.getTime());
        
        int billId = 111+ (int)(Math.random()*55);
        
        jTextArea1.append("               Thanks for visiting\n *********************************\n"+
                                  "                Bill Id : " + billId + "\n" +
                                  "                Date :  " + D.format(c.getTime()) +"\t" + "Time : " + T.format(c.getTime()) +"\n" +
                                  "                Price for Food : " + jTextField8.getText() + "\n" +
                                  "                Price for Fluid : " + jTextField9.getText() + "\n" + 
                                  "                Total Bill : " + jTextField10.getText() +"\n" +
                                  "                *********************************");
        
       
    }//GEN-LAST:event_jButton2ActionPerformed

